INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254161, 'Cana Brava Resort', 'O Cana Brava Resort é um destino completo de férias, na mais bela praia de Ilhéus, sul da Bahia. Aqui a diversão não tem hora para acabar, com atividades diárias para adultos e crianças. Além disso, nossos hóspedes aproveitam de deliciosas opções do serviço all inclusive, e realizam uma verdadeira viagem gastronômica por diferentes culinárias. Tudo isso em meio à paisagens encantadoras que irão te surpreender.', -14.994043646307352, -38.99990493168996, 5, 7, 1, 1);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254162, 'Rio Othon Palace', 'Localizado à beira-mar da praia de Copacabana, o Rio Othon Palace oferece piscina na cobertura, com uma vista excelente do Pão de Açúcar, e academia no local. O café da manhã é servido no 3º andar, com vista panorâmica do mar. Para sua comodidade, a propriedade dispõe de Wi-Fi gratuito em todas as áreas. Todas as acomodações do Rio Othon Palace incluem ar-condicionado, varanda, TV a cabo e frigobar.', -22.977230583418038, -43.18916394605217,  5, 7, 1, 4);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254163, 'You Stay at Vila Olimpia', 'Com vista da cidade, o You Stay at Vila Olimpia - ITC está localizado em São Paulo e oferece restaurante, serviço de quarto, bar, jardim, piscina ao ar livre aberta o ano todo e terraço ao ar livre. O apart-hotel oferece Wi-Fi e estacionamento privativo gratuitos. O You Stay at Vila Olimpia - ITC oferece área de estar com TV de tela plana e banheiro privativo com amenidades de banho gratuitas, secador de cabelo e chuveiro.', -23.594820142324142, -46.68789471610566, 5, 6, 1, 3);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254164, 'Bugan Recife Boa Viagem Hotel', 'Oferecendo piscina ao ar livre e academia, o Bugan Recife Hotel by Atlantica está localizado em Recife. O Wi-Fi gratuito está à sua disposição. A Praia da Boa Viagem fica a 400 metros deste hotel.', -8.128622962139861, -34.90154640406308, 4, 8, 1, 5);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254165, 'ibis Styles Moinhos de Vento', 'A propriedade fica a 8,6 km do Estádio Beira Rio, O ibis Styles Porto Alegre Moinhos de Vento oferece acomodações 3 estrelas em Porto Alegre e dispõe de um bar. Este hotel 3 estrelas dispõe de academia e quartos com ar-condicionado, Wi-Fi gratuito e banheiro privativo. A propriedade é para não fumantes e está situada a 39 km da Estação Rodoviária de Novo Hamburgo.', -30.022183711001247, -51.19353763477609, 3, 9, 1, 8);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254166, 'Pousada Tartaruga Praia de Pipa', 'A Pousada Tartaruga fica na Rua Principal, na Praia de Pipa, conhecida por suas ondas ideais para a prática de surfe. Dispõe de uma piscina ao ar livre situada em jardins floridos e palmeiras tropicais. O estacionamento e o Wi-Fi estão disponíveis gratuitamente.', -6.228030042336687, -35.05128269719976, 3, 9, 3, 15);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254167, 'Assel Pousada Botanico', 'A Assel Pousada Botanico, com WiFi gratuito em todas as áreas, está localizada a apenas 900 m do Jardim Botânico de Curitiba. Durante sua estadia na Assel Pousada Botanico, você pode utilizar a cozinha compartilhada com eletrodomésticos, utensílios e talheres. A sala de estar dispõe de varanda com vista para a cidade.', -25.441994362661497, -49.25010771521252, 3, 9, 3, 14);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254168, 'Pousada Suítes Do Pelô', 'A Pousada Suítes do Pelô está situada no centro histórico de Salvador, a apenas 20 km do Aeroporto Luís Eduardo Magalhães. A propriedade oferece acomodações básicas com WiFi gratuito.', -12.9739052348436, -38.507532193842934, 2, 8, 3, 2);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254169, 'Pousada Vitória da Vila', 'Situada em Brasília, a 3,6 km do Palácio da Justiça, a Pousada Vitória da Vila oferece lounge compartilhado, quartos para não fumantes e Wi-Fi gratuito em todas as áreas. A propriedade fica a cerca de 4,2 km do Congresso Nacional do Brasil, a 4,4 km da Praça dos Três Poderes e a 4,7 km da Catedral de Brasília. A pousada dispõe de quartos família.', -15.792285925577112, -47.846946819236585, 3, 9, 3, 6);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254170, 'Casa da Adriana Pousada', 'Situada em Fortaleza, a 2,1 km da Praia do Meireles, a Casa da Adriana Pousada oferece acomodações com jardim, estacionamento privativo gratuito e terraço. Localizada a cerca de 2,8 km da Praia de Iracema, a pousada com Wi-Fi gratuito também fica a 8,2 km do North Shopping. Todos os quartos possuem varanda.', -3.7424884494042674, -38.503574632074454, 3, 8, 3, 7);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254171, 'Grand Oca Maragogi All Inclusive Resort', 'Localizado em frente à Praia Ponta de Mangue, o Grand Oca fica a 350 metros das piscinas naturais e oferece Wi-Fi gratuito nas áreas comuns. Todos os quartos Standard incluem varanda e ar-condicionado. Os bangalôs do Grand Oca Maragogi dispõem de ar-condicionado e vista panorâmica do jardim ou da piscina.', -8.958979715392777, -35.178164703645855, 3, 9, 2, 16);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254172, 'HOTEL RESORT RIO POTY', 'Localizado em São Luís, a 1 km da Praia de São Marcos, o HOTEL RESORT RIO POTY oferece acomodações com jardim, estacionamento privativo gratuito, terraço e restaurante. Este hotel 4 estrelas dispõe de bar e quartos com ar-condicionado, Wi-Fi gratuito e banheiro privativo. A acomodação dispõe de serviço de quarto, recepção 24 horas e depósito para bagagem.', -2.4848170447506965, -44.305058662920416, 4, 9, 2, 17);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254173, 'Serhs Natal Grand Hotel & Resort', 'O Serhs Natal Grand Hotel é um resort à beira-mar localizado entre as praias de Ponta Negra e Areia Preta. Este complexo oferece 4 piscinas, 3 bares, 5 restaurantes, uma variedade de refeições e bebidas e opções de entretenimento. O Wi-Fi gratuito está à sua disposição em todas as áreas.', -5.8547100992022125, -35.181660574557, 4, 8, 2, 4);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254174, 'Amazônia Golf Resort', 'O Amazonia Golf Resort By Nobile está localizado a 59 km do Aeroporto Internacional de Manaus - Eduardo Gomes. O resort conta com internet Wi-Fi grátis e campo oficial de golfe. As acomodações estão equipadas com ar-condicionado split, TV LCD, cofre e frigobar. Os banheiros são privativos e contam com secador de cabelos.', -2.7052572229366443, -59.726012916288795, 3, 7, 2, 13);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254175, 'Bourbon Thermas Eco Resort', 'Idealmente localizado na fronteira entre Brasil, Argentina e Paraguai, este hotel resort 5 estrelas para não fumantes oferece acomodações modernas a 22 km das Cataratas do Iguaçu. O WiFi está disponível gratuitamente em toda a propriedade.', -25.5597460310984, -54.559583033925996, 5, 9, 2, 10);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254176, 'Brazilodge All Suites Hostel', 'Situado a apenas 800 m da Estação de Metrô Ana Rosa, em São Paulo, o Brazilodge All Suites tem uma cozinha de uso comum, um salão de jogos e Wi-Fi gratuito. O albergue também conta com um buffet de café-da-manhã diário e acomodações com ar-condicionado.', -23.586052633371224, -46.6436312331994, 2, 9, 4, 16);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254177, 'Zili Pernambuco - Hostel Pousada', 'Com jardim, lounge compartilhado, bar e Wi-Fi gratuito, o Zili Pernambuco - Hostel Pousada está situado em Recife, a 16 km do Shopping Guararapes e a menos de 1 km do Museu de Arte Moderna Aloísio Magalhães. A propriedade está localizada a 1,4 km da Ponte Buarque de Macedo, a 2,2 km da Ponte Giratória - Ponte 12 de Setembro e a 2,5 km do Porto de Recife. A acomodação oferece karaokê e cozinha compartilhada.', -8.056443789709505, -34.88548555961083, 2, 8, 4, 5);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254178, 'Rota BH Hostel', 'O Rota BH Hostel está localizado em Belo Horizonte, a 1,1 km da Estação Rodoviária de Belo Horizonte. Os quartos oferecem acesso a um banheiro compartilhado. O Rota BH Hostel também dispõe de Wi-Fi gratuito em todos os ambientes. O Rota BH fica a 7 km do Estádio Mineirão e a 8 km da Igreja de São Francisco de Assis. Já o Aeroporto Carlos Drummond de Andrade fica a 8 km do local.', -19.920023054435024, -43.94893065926679, 2, 8, 4, 9);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254179, 'Nomads Hostel Multicultural', 'O Nomads Hostel Multicultural & Coworking, em Salvador, oferece acomodações apenas para adultos com jardim e bar. Dispondo de terraço ao ar livre, esta propriedade está localizada a uma curta distância do Porto da Barra, da Praia do Farol da Barra e do Farol da Barra. A acomodação oferece cozinha compartilhada e Wi-Fi gratuito.', -13.002389075107768, -38.52822185722651, 2, 7, 4, 2);
INSERT INTO produtos(uid, nome, descricao, latitude, longitude,qualificacao,classificacao,categoria_id, cidade_id) VALUES(2305222254180, 'Hostel Chocolatchê', 'Convenientemente localizado em Gramado, o Hostel Chocolatchê oferece buffet de café da manhã e Wi-Fi gratuito em todas as áreas. A propriedade fica a 2,6 km do Lago Negro de Gramado, a 8,2 km da Catedral de Pedra e a 34 km do Parque do Vale do Imigrante. A acomodação oferece lounge compartilhado, serviço de concierge e organização de passeios.', -29.37700136147912, -50.87500390190019, 4, 8, 4, 11);
